import React, { useState, useEffect, useRef } from 'react';
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, /*NavItem, NavLink,*/ UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem, NavbarText } from 'reactstrap';
import '../../css/Header.css'
import Mario from '../../images/mario.gif';
import Cursormove from '../../sound/cursormove.mp3';

const Header = () => {

    function useAudio() {
        const ref = useRef()
        return [ref]
    }

    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);
    const [audio, setAudio] = useState(false, Cursormove)

    const play = () => setAudio(true)
    const stop = () => setAudio(false)

    useEffect(() => {
        ref.current.addEventListener('mouseenter', play)
        ref.current.addEventListener('mouseleave', stop)
        return () => {
            ref.current.removeEventListener('mouseenter', play)
            ref.current.removeEventListener('mouseleave', stop)
        }
    }, [ref])


    return (
        <div>
            <Navbar className="nghead" light expand="md">
                <NavbarToggler onClick={toggle} />
                <Collapse isOpen={isOpen} navbar>
                    <Nav className="mr-auto" navbar>
                        <UncontrolledDropdown nav inNavbar>
                            <DropdownToggle className="menutitle" nav caret>Choisissez votre difficult√©</DropdownToggle>
                            <DropdownMenu right>
                                {audio && <DropdownItem className="drophead">News</DropdownItem>}
                                <DropdownItem className="drophead">Tests</DropdownItem>
                                <DropdownItem className="drophead">Trucs et astuces</DropdownItem>
                                <DropdownItem className="drophead">Blog</DropdownItem>
                                <DropdownItem className="drophead">Forum?</DropdownItem>
                                <DropdownItem className="drophead">Liens</DropdownItem>
                            </DropdownMenu>
                        </UncontrolledDropdown>
                    </Nav>
                    <img className="mario" src={Mario} alt="" />
                    <NavbarBrand className="navtitle" href="/">New Game Plus</NavbarBrand>
                    <NavbarText>
                        <a className="nes-btn" href="https://www.youtube.com/user/MrOhzora">
                            <i className="nes-icon youtube is-small" /><a className="titleyt">Youtube</a>
                        </a>
                    </NavbarText>
                </Collapse>
            </Navbar>
        </div>
    );
}

export default Header;
